// Gera um CNPJ válido com 14 dígitos sem formatação

function gerarCNPJ() {
  function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  const n = [];
  for (let i = 0; i < 8; i++) {
    n.push(getRandomInt(0, 9));
  }

  // Fixo: 0001 (filial padrão)
  n.push(0, 0, 0, 1);

  const peso1 = [5, 4, 3, 2, 9, 8, 7, 6, 5, 4, 3, 2];
  let soma = n.reduce((acc, val, idx) => acc + val * peso1[idx], 0);
  let d1 = soma % 11;
  d1 = d1 < 2 ? 0 : 11 - d1;
  n.push(d1);

  const peso2 = [6].concat(peso1);
