function formataTelefone(number) {
    number = number.toString().replace(/[^\d]/g, "");
    let ddi = "55";
    let ddd, telefone;

    if (number.length === 13) {
        ddi = number.substring(0, 2);
        ddd = number.substring(2, 4);
        telefone = number.substring(4);
    } else if (number.length === 12) {
        if (number.startsWith("55")) {
            ddd = number.substring(2, 4);
            telefone = number.substring(4);
        } else if (number.startsWith("0")) {
            return formataTelefone(number.substring(1));
        } else {
            return { comNonoDigito: number, semNonoDigito: number };
        }
    } else if (number.length === 11) {
        ddd = number.substring(0, 2);
        telefone = number.substring(2);
    } else if (number.length === 10) {
        ddd = number.substring(0, 2);
        telefone = "9" + number.substring(2);
    } else {
        return { comNonoDigito: number, semNonoDigito: number };
    }

    let telefoneComNono = telefone.length === 8 ? "9" + telefone : telefone;
    let telefoneSemNono = telefoneComNono.startsWith("9") ? telefoneComNono.substring(1) : telefoneComNono;

    return {
        comNonoDigito: ddi + ddd + telefoneComNono,
        semNonoDigito: ddi + ddd + telefoneSemNono
    };
}

let numerosFormatados = formataTelefone($input.first().json.telefone);

$input.all()[0].json["numerosFormatados"] = numerosFormatados
return $input.all();

