// Gera um CNPJ v√°lido com 14 d√≠gitos sem formata√ß√£o

function gerarCNPJ() {
  function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  const n = [];
  for (let i = 0; i < 8; i++) {
    n.push(getRandomInt(0, 9));
  }

  // Fixo: 0001 (filial padr√£o)
  n.push(0, 0, 0, 1);

  const peso1 = [5, 4, 3, 2, 9, 8, 7, 6, 5, 4, 3, 2];
  let soma = n.reduce((acc, val, idx) => acc + val * peso1[idx], 0);
  let d1 = soma % 11;
  d1 = d1 < 2 ? 0 : 11 - d1;
  n.push(d1);

  const peso2 = [6].concat(peso1);
  soma = n.reduce((acc, val, idx) => acc + val * peso2[idx], 0);
  let d2 = soma % 11;
  d2 = d2 < 2 ? 0 : 11 - d2;
  n.push(d2);

  return n.join('');
}

// Retorna como sa√≠da
return [
  {
    json: {
      cnpj: gerarCNPJ()
    }
  }
];
