const datesArray = $input.all()[0].json["appended_formattedDate"]

function generateWhatsAppMessage(datesArray) {
  let message = "*Report Diário*\n\nSegue o resumo de horários disponíveis para o *CG da Carol - Bootcamp e Acelera*:\n\n";

  datesArray.forEach(dateObj => {
    message += `${dateObj}\n`;
  });

  return message;
}

const whatsappMessage = generateWhatsAppMessage(datesArray);

return {whatsappMessage}

