function verificarHoraUtil(dataString) {
    // Converte a string de data para um objeto Date
    var data = new Date(dataString);

    // Obtém o dia da semana (0 = Domingo, 1 = Segunda, ..., 6 = Sábado)
    var diaSemana = data.getDay();

    // Obtém a hora atual
    var horaAtual = data.getHours();

    // Verifica se não é sábado ou domingo e se a hora está entre 7 da manhã e 8 da noite
    if (diaSemana !== 0 && diaSemana !== 6 && horaAtual >= 7 && horaAtual < 20) {
        return true;
    } else {
        return false;
    }
}

var dataAtual = $input.all()[0].json['currentDate']

var isValid = (verificarHoraUtil(dataAtual));

return {isValid}

function isWeekend(dateString) {
    // Cria um objeto Date a partir da string de data recebida
    const date = new Date(dateString);

    // Obtém o dia da semana (0 = Domingo, 1 = Segunda-feira, ..., 6 = Sábado)
    const dayOfWeek = date.getDay();

    // Verifica se o dia da semana é Sábado (6) ou Domingo (0)
   return (dayOfWeek === 6 || dayOfWeek === 0) ? true : false;
}

// Exemplo de uso
const dateStr = $('Data Atual').all()[0].json['currentDate'];
const isFds = isWeekend(dateStr);

return {isFds}
