function addBusinessDays(startDateStr) {
  // Parse the input date string into a Date object
  let startDate = new Date(startDateStr);

  // Set the time to the start of the day (midnight)
  startDate.setHours(0, 0, 0, 0);

  // Initialize the final date as the start date
  let finalDate = new Date(startDate);

  // Function to add a business day
  const addBusinessDay = (date) => {
    date.setDate(date.getDate() + 1);
    if (date.getDay() === 6) { // If Saturday
      date.setDate(date.getDate() + 2);
    } else if (date.getDay() === 0) { // If Sunday
      date.setDate(date.getDate() + 1);
    }
  };

  // Special rules for Thursday and Friday
  if (startDate.getDay() === 4) { // If Thursday
    finalDate.setDate(startDate.getDate() + 5); // Move to next Tuesday
  } else if (startDate.getDay() === 5) { // If Friday
    finalDate.setDate(startDate.getDate() + 5); // Move to next Wednesday
  } else {
    // Add three business days to the final date
    for (let i = 0; i < 3; i++) {
      addBusinessDay(finalDate);
    }
  }

  // Format the dates back to ISO strings
  const initialDateStr = startDate.toISOString();
  const finalDateStr = finalDate.toISOString();

  // Return the initial and final dates
  return {
    initialDate: initialDateStr,
    finalDate: finalDateStr
  };
}

