function extrairPrimeiroUUID(url) {
    // Expressão regular para encontrar UUIDs
    const regexUUID = /[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}/;

    // Executa a expressão regular na URL
    const match = url.match(regexUUID);

    // Verifica se encontrou um UUID e retorna, senão retorna nulo
    return match ? match[0] : null;
}

const url = $node["Trata Webhook"].json["form_response"]["answers"]["9"]["url"]
